package day2;

public class ArithmeticOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=15,b=4;
		System.out.println("a + b =" + (a + b));
		System.out.println("a - b =" + (a - b));
		System.out.println("a * b =" + (a * b));
		System.out.println("a / b =" + (a / b));//(integer division, fractional part ignored)
		System.out.println("a % b =" + (a % b));//(remainder of division)
	}

}
