package day2;

public class RelationalOperators { 

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10,b=20;
		System.out.println("a="+a+ ",b=" +b);
		System.out.println("a==b:" +(a==b));
		System.out.println("a!=b:" +(a!=b));
		System.out.println("a>b:" +(a>b));
		System.out.println("a<b:" +(a<b));
		System.out.println("a>=b:" +(a>=b));
		System.out.println("a<=b:" +(a<=b));
	}

}
